INSERT INTO pdf_pages VALUES(1452,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',14,replace('7.2\n\nSCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_14.png');
INSERT INTO pdf_pages VALUES(1453,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',15,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG\n\n− In TCP/IP-Netzwerken ist eine Firewall (i.e.S.) wie ein Router ein eigenständiges Gerät, das \n\nmehrere (einheitliche) Netzwerke miteinander verbindet.\n\n− Zusätzlich zu den Funktionalitäten eines Routers bietet aber eine Firewall auch Schutz vor \n\nunerlaubter Datenübertragung, indem sie jedes ein- und ausgehende Datagramm \nanalysiert und – entsprechend vordefinierter Regeln – die Weiterleitung des Datagramms \nvornimmt oder unterbindet.\n\n04.07.2024\n\n15\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_15.png');
INSERT INTO pdf_pages VALUES(1454,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',16,replace('„Ein Sicherheitsgateway (oft auch Firewall \ngenannt) ist ein System aus soft- und \nhardwaretechnischen Komponenten. \nEs gewährleistet die sichere Kopplung von IP-\nNetzen durch Einschränkung der technisch \nmöglichen auf die in einer \nSicherheitsrichtlinie als ordnungsgemäß \ndefinierte Kommunikation“\n\nQuelle: https://www.bsi.bund.de/SharedDocs/Glossareintraege/DE/S/Sicherheitsgateway.html\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_16.png');
INSERT INTO pdf_pages VALUES(1455,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',17,replace('„Sicherheit bei der Netzkopplung bedeutet \nhierbei im Wesentlichen, dass ausschließlich \nerwünschte Zugriffe oder Datenströme \nzwischen verschiedenen Netzen zugelassen \nund die übertragenen Daten kontrolliert \nwerden.“\n\nQuelle: https://www.bsi.bund.de/SharedDocs/Glossareintraege/DE/S/Sicherheitsgateway.html\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_17.png');
INSERT INTO pdf_pages VALUES(1456,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',18,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG\n\n− Eine DMZ (Demilitarisierte Zone) ist ein Zwischennetz, das an Netzübergängen gebildet \n\nwird und sowohl von innen als auch von außen erreichbar ist. \n− weniger stark gesichert als das interne Netz, \n− besser vom äußeren Netz aus erreichbar. \n\n− Sie dient der Schaffung eines zusätzlichen Sicherheitsbereichs für Dienste (z. B. E-Mail, \n\nWeb) oder Proxys\n− die von externen Netzen aus nutzbar sein sollen\n− aus Sicherheitsgründen nicht im internen Netz platziert werden dürfen.\n\n04.07.2024\n\n18\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_18.png');
INSERT INTO pdf_pages VALUES(1457,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',19,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG\n\n− Eine Firewall besteht aus einer Softwarekomponente, die Netzwerkpakete lesen und \n\nauswerten kann.\n\n− Die Software kann\n\n− auf einer zu schützenden Hardwarekomponente selbst oder\n− auf einer separaten, nur für die Firewall vorgesehenen Hardware installiert sein. \n\n− Innerhalb der Software sind Regeln definiert, welche Datenpakete durchgelassen werden \n\nund welche zu blockieren sind.\n\n04.07.2024\n\n19\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_19.png');
INSERT INTO pdf_pages VALUES(1458,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',20,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG\nWas kann eine Firewall?\n\n04.07.2024\n\n20\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_20.png');
INSERT INTO pdf_pages VALUES(1459,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',21,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG\nWas kann eine Firewall?\n\n− Prüfung des Datenverkehrs auf Basis von Regeln und Richtlinien\n\n− Protokollierung und Alarmierung\n\n− Abwehr von Einbruchsversuchen\n\n− Abwehr von Denial of Service Angriffen (DoS)\n\n04.07.2024\n\n21\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_21.png');
INSERT INTO pdf_pages VALUES(1460,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',22,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG \nWas kann eine Firewall NICHT?\n\n− Vor bösartigen Insidern schützen,\n\n− Vor Verbindungen schützen, die nicht über sie geführt werden,\n\n− Endgeräte-Sicherheit ersetzen,\n\n− Sichere Kommunikationsverfahren ersetzen (Verschlüsselung etc.),\n\n− Sich selbst richtig konfigurieren …. \n\n04.07.2024\n\n22\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_22.png');
INSERT INTO pdf_pages VALUES(1461,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',23,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG \n\n− Ist die Software auf einer separaten, nur für die Firewall vorgesehenen Hardware installiert, \n\nwird die Firewall auch als externe oder als Hardware-Firewall bezeichnet.\n\n− Häufig sind Firewalls an Netzwerkgrenzen zwischen einem internen und einem externen \nNetzwerk platziert. An dieser zentralen Stelle kontrollieren Sie den ein- und ausgehenden \nDatenverkehr.\n\nIn der Regel kontrolliert eine Firewall den Datenverkehr an besonders \nkritischen Stellen in Netzwerken zum Einsatz. Als somit zentralem Bestandteil \neiner sicheren IT-Infrastruktur muss der Beschaffung, der Konfiguration und \ndem Betrieb einer Firewall besondere Aufmerksamkeit zukommen.\n\n04.07.2024\n\n23\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_23.png');
INSERT INTO pdf_pages VALUES(1462,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',24,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG\n\n04.07.2024\n\n24\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_24.png');
INSERT INTO pdf_pages VALUES(1463,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',25,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG \n\n− Um zudem unerlaubte Datenübertragungen auch über verschlüsselte Tunnel zu erkennen, \nist die Fähigkeit des Aufbrechens von TLS/SSL-Verbindungen durch eine Firewall mit \nanschließender Regelanwendung heutzutage meist unverzichtbar.\n\n− Im Betrieb muss eine Firewall die Möglichkeit bieten, die Vertraulichkeit der \n\ndurchgeleiteten Daten sicherzustellen.\n\n− Dazu muss sie Verschlüsselungen mit modernen Algorithmen und sicheren Parametern \n\nermöglichen. Sie muss zudem die Möglichkeit bieten, dass Sende- und Empfangsaktivitäten \nnur nach einer „starken“ Authentisierung des Anwenders gestattet werden.\n\n− Der Zugang zur Konfiguration der Firewall muss durch verschiedene Maßnahmen \n\nbesonders geschützt werden.\n\n04.07.2024\n\n25\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_25.png');
INSERT INTO pdf_pages VALUES(1464,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',26,replace('SCHUTZ VOR UNERLAUBTER DATENÜBERTRAGUNG \n\n− Quelle Abbildung: BSI, 2022\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_26.png');
INSERT INTO pdf_pages VALUES(1465,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',27,replace('7.3\n\nSCHUTZ VOR UNERWÜNSCHTEM DATENVERKEHR\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_27.png');
INSERT INTO pdf_pages VALUES(1466,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',28,replace('SCHUTZ VOR UNERWÜNSCHTEM DATENVERKEHR\n\n− DoS (Denial of Service)-Angriffe zielen darauf ab, Netzwerke und damit auch die in den \n\nNetzwerken angebotenen Dienste lahmzulegen. \n− Dies wird typisch dadurch erreicht, dass ein im anzugreifenden Netzwerk angebotener \n\nDienst so stark mit Anfragen „bombardiert“ wird, dass er regulären Anfragen nicht mehr \nnachkommen kann.\n\n− oft Datenraten von über 300 Gigabit pro Sekunde beobachtet\n\n− Bei Distributed DoS-Angriffen (DDoS) wird ein DoS-Angriff von mehreren Teilnehmern \n\ngleichzeitig ausgeführt. \n− DDoS-Angriffe können auch von einem Bot-Netz ausgeführt werden. \n− Meistens wissen die Besitzer gar nicht, dass ihre Geräte Teil eines Bot-Netzes sind.\n\n− DoS- und DDoS-Angriffe sind nach deutschem Recht strafbar (§ 303b StGB \n\nComputersabotage)\n\n04.07.2024\n\n28\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_28.png');
INSERT INTO pdf_pages VALUES(1467,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',29,replace('7.4\n\nSCHUTZ DURCH NOTFALLPLANUNG\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_29.png');
INSERT INTO pdf_pages VALUES(1468,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',30,replace('SCHUTZ DURCH NOTFALLPLANUNG\n\n−\n\n−\n−\n\n−\n\nZu der Notfallplanung eines IT- bzw. Informationsverbundes gehört jegliche Vorsorge zur \nAufrechterhaltung oder Wiederherstellung von IT-Anwendungen im Falle unvorhergesehener \nEreignisse oder Störungen.\n\nEine Notfallplanung erfordert ein systematisches Vorgehen.\nDurchgeführt werden sollte zunächst eine Business Impact Analysis (BIA):\n\nzu verstehen, welche IT-Anwendungen wichtig für die Aufrechterhaltung des Geschäftsbetriebs \nund damit für die Institution sind und welche Folgen ein Ausfall dieser IT-Anwendungen haben \nkann\nEs existieren viele Methoden und Wege, eine BIA durchzuführen\n\n−\n− Wie die erforderlichen Ergebnisse ermittelt werden, kann jede Institution für sich entscheiden\n−\nIn BSI-Standard 100–4 (vgl. Bundesamt für Sicherheit in der Informationstechnik 2008) wird \nbeispielsweise eine Methode vorgestellt, die an die Schutzbedarfsfeststellung nach BSI-\nStandard 200–2 (vgl. Bundesamt für Sicherheit in der Informationstechnik (2017b) angelehnt ist\n\n04.07.2024\n\n30\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_30.png');
INSERT INTO pdf_pages VALUES(1469,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',31,replace('SCHUTZ DURCH NOTFALLPLANUNG\nBusiness Impact Analysis\n\n− Welche Geschäftsprozesse sind kritisch für die Aufrechterhaltung des Geschäftsbetriebs und damit für die Institution?\n− Welche Folgen hat ein Ausfall dieser Geschäftsprozesse?\n\n− Es ist zu analysieren und zu bewerten, wie sich ein Ausfall von Geschäftsprozessen auf die Institution auswirken und wie \n\nsich Schäden während dieser Zeit entwickeln können\n\n− Für die Geschäftsprozesse sind die Wiederanlaufparameter zu identifizieren bzw. festzulegen. Dazu zählen: \n\n− die maximal tolerierbare Ausfallzeit,\n− die Wiederanlaufzeit,\n− das Wiederanlaufniveau und\n− der maximal zulässige Datenverlust.\n\n− Mit den Ergebnissen der BIA erfolgt anschließend eine Risikoanalyse, die eine Maßnahmenauswahl und eine \n\nPriorisierung dieser Maßnahmen zum Ziel hat. Diese münden in eine Notfallplanung, die es umzusetzen gilt.\n\n04.07.2024\n\n31\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_31.png');
INSERT INTO pdf_pages VALUES(1470,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',32,replace('SCHUTZ DURCH NOTFALLPLANUNG\n\n− Bei der Notfallplanung sind selbstverständlich der Datenschutz und die IT-Sicherheit zu \n\nberücksichtigen.\n\n− Es ist sicherzustellen, dass im Falle eines Ausfalls, bei der Inbetriebnahme und dem Betrieb von \nAusweichlösungen und bei der Wiederaufnahme des Normalbetriebs sowohl Datenschutz als \nauch IT-Sicherheit gewährleistet sind.\n− die Gewährleistung der Vertraulichkeit von Daten (z. B. Zugriffsrechte, Verschlüsselung)\n− Einhaltung der Minimalanforderungen an die Datensicherung \n− Einhaltung gesetzlicher Vorgaben\n\n− Die Möglichkeiten zur Überprüfung der eigenen Notfallplanung sind vielfältig:\n\n− Sie reichen von einfachen Überprüfungen von Einzelmaßnahmen bis hin zu komplexen Übungen\n\n04.07.2024\n\n32\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_32.png');
